<script setup>
import { ref, onMounted } from 'vue';
import axios from 'axios';
// 定义detail变量
const detail = ref('');
const messageType = ref(0);
const setType = (type) => {
  console.log("type=======", type)
  messageType.value = type;
};

const getMessage = async () => {
  try {
    let response;

    if (messageType.value == '5') {

      response = await axios.get('https://api.vvhan.com/api/sao');

      detail.value = response.data;
    } else {
      response = await axios.get('/api/message/get_message?type=' + messageType.value);
      detail.value = response.data.data;
    }

  } catch (error) {
    console.error('Failed to fetch detail:', error);
  }
};


onMounted(async () => {
  try {
    const response = await axios.get('/api/message/get_message?type=' + messageType.value);
    detail.value = response.data.data;
  } catch (error) {
    console.error('Failed to fetch detail:', error);
  }
});



</script>

